<template>
  <div>
    <a-card class="cart-element-container" :bordered="false">
      <a-card-grid class="cart-detail-cover" :hoverable="false"
        ><div class="cart-table-left">
          <a-button
            class="toggle-cart-button"
            v-if="isLoggedIn && getUser.type === 0"
            @click="toggleCart"
            ><CheckCircleOutlined
              style="font-size: 0.6vw; line-height: 0.6vw; margin: auto"
              v-if="!isInCart"
            /><CloseCircleOutlined style="font-size: 0.6vw; line-height: 0.6vw" v-else />{{
              toggleCartLabel
            }}</a-button
          >
          <img
            class="cover-image"
            :src="result.smallImg"
            onerror="this.src='data:image/png;base64,'"
          /></div
      ></a-card-grid>
      <a-card-grid class="cart-detail-title" :hoverable="false">
        <div class="cart-detail-title-meta">{{ result.type }} / {{ result.method }}</div>
        <div class="cart-detail-title-name">{{ result.name }}</div>
        <div class="cart-detail-title-calorie">{{ result.calorie }} 칼로리</div>
      </a-card-grid>
    </a-card>
    <a-card class="cart-detail-description-ingredients">
      <a-list
        :grid="{ gutter: 16, xs: 1, sm: 2, md: 4, lg: 4, xl: 6, xxl: 3 }"
        :data-source="result.ingredients"
      >
        <template #renderItem="{ item }">
          <a-list-item>
            <a-input
              type="text"
              placeholder="(g/mL)"
              style="{text-align:right;}"
              :defaultValue="item.unit"
              disabled
            >
              <template #addonBefore>{{ item.name }}</template>
              <template #addonAfter> (g/mL) </template>
            </a-input>
          </a-list-item>
        </template>
      </a-list>
    </a-card>
  </div>
</template>

<script>
import UserInfo from '../../store/models/UserInfo.js';
import { CheckCircleOutlined, CloseCircleOutlined } from '@ant-design/icons-vue';
export default {
  emits: ['show-error'],
  props: ['result', 'bordered'],
  components: {
    CheckCircleOutlined,
    CloseCircleOutlined
  },
  computed: {
    getUser() {
      if (UserInfo.all().length === 0) {
        return {
          userId: null,
          nickname: null,
          token: null,
          favoriteRecipe: []
        };
      } else {
        return UserInfo.all()[0];
      }
    },
    isLoggedIn() {
      return !!this.getUser.token;
    },
    userFavoriteRecipe() {
      return Array.from(this.getUser.favoriteRecipe);
    },
    isInCart() {
      if (this.userFavoriteRecipe.length > 0) {
        for (let index in this.userFavoriteRecipe) {
          if (this.userFavoriteRecipe[index] == this.result.id) {
            return index;
          }
        }
      }
      return false;
    },
    toggleCartLabel() {
      if (this.isInCart) {
        return '장바구니에서 제외';
      } else {
        return '장바구니에 추가';
      }
    }
  },
  methods: {
    showError(error) {
      this.$emit('show-error', error);
    },
    async toggleCart() {
      UserInfo.commit((state) => {
        state.fetching = true;
      });
      let prevFavoriteRecipe = Array.from(UserInfo.all()[0].favoriteRecipe);
      if (this.isInCart !== false) {
        prevFavoriteRecipe.splice(this.isInCart, 1);
        if (prevFavoriteRecipe === []) {
          prevFavoriteRecipe = '';
        }
        const payload = {
          userId: this.getUser.userId,
          token: this.getUser.token,
          data: { favoriteRecipe: prevFavoriteRecipe }
        };
        const fetchResult = await UserInfo.api().toggleCart(payload);
        if (fetchResult.response.data.error) {
          this.showError('레시피를 장바구니에서 삭제하는 데 실패하였습니다!');
        }
      } else {
        if (this.userFavoriteRecipe.length === 0) {
          prevFavoriteRecipe = [];
        }
        prevFavoriteRecipe.push(+this.result.id);
        const payload = {
          userId: this.getUser.userId,
          token: this.getUser.token,
          data: { favoriteRecipe: prevFavoriteRecipe }
        };
        const fetchResult = await UserInfo.api().toggleCart(payload);
        if (fetchResult.response.data.error) {
          this.showError('레시피를 장바구니에 추가하는 데 실패하였습니다!');
        }
      }
      const fetchResult = await UserInfo.api().fetch(this.getUser.userId, this.getUser.token);
      if (fetchResult.response.data.error) {
        this.showError('사용자 정보를 불러올 수 없습니다!');
      }
      UserInfo.commit((state) => {
        state.fetching = false;
      });
    }
  }
};
</script>

<style scoped>
@import url('https://fonts.googleapis.com/css2?family=Jua&display=swap');
.cart-element-container {
  box-shadow: 0 2px 6px 0 rgb(0 0 0 / 15%);
  width: 100%;
}

.cart-detail-cover {
  width: 40%;
  height: 25vh;
  text-align: center;
  padding: 0;
}

.cart-table-left {
  display: inline-block;
  width: 100%;
  position: relative;
}

.toggle-cart-button {
  position: absolute;
  width: fit-content;
  height: fit-content;
  padding-left: 0.3vw;
  padding-right: 0.3vw;
  top: 0.3vh;
  right: 0.1vw;
  font-size: 0.6vw;
  line-height: 0.6vw;
}

.cover-image {
  display: block;
  margin: 0px auto;
  width: 100%;
  height: 25vh;
}

.cart-detail-title {
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  width: 60%;
  height: 25vh;
  text-align: center;
}

.cart-detail-title-meta,
.cart-detail-title-name,
.cart-detail-title-calorie {
  width: 100%;
  text-align: left;
  margin: 0;
}

.cart-detail-title-meta {
  font-size: 0.8vw;
  line-height: 0.8vw;
  color: #f47b0f;
  font-weight: bold;
}

.cart-detail-title-name {
  font-size: 2.8vw;
  line-height: 2.8vw;
  text-align: center;
  font-family: 'Jua', sans-serif;
}

.cart-detail-title-calorie {
  font-size: 1.5vw;
  line-height: 1.5vw;
  color: #f47b0f;
  text-align: right;
  font-weight: bold;
}

.cart-detail-description > p {
  margin: 0;
}
</style>
