<template>
  <a-card class="result-element-container" bodyStyle="width:100%" bordered @click="showDetail">
    <template #cover>
      <div class="card-cover-container">
        <a-button
          class="toggle-cart-button"
          v-if="isLoggedIn && getUser.type === 0"
          @click="
            (event) => {
              toggleCart();
              event.stopImmediatePropagation();
            }
          "
          ><CheckCircleOutlined
            style="font-size: 16px; vertical-align: middle; margin: auto"
            v-if="!isInCart"
          /><CloseCircleOutlined style="font-size: 16px; vertical-align: middle" v-else />{{
            toggleCartLabel
          }}</a-button
        >
        <a-button
          class="update-recipe-button"
          v-if="getUser.type === 1 || (isLoggedIn && getUser.type === 0 && isCreator)"
          @click="
            (event) => {
              updateRecipe();
              event.stopImmediatePropagation();
            }
          "
          ><EditOutlined
            style="font-size: 16px; vertical-align: middle; margin: auto"
          />편집</a-button
        >
        <a-button
          class="delete-recipe-button"
          v-if="getUser.type === 1 || (isLoggedIn && getUser.type === 0 && isCreator)"
          @click="
            (event) => {
              deleteRecipe();
              event.stopImmediatePropagation();
            }
          "
          ><DeleteOutlined
            style="font-size: 16px; vertical-align: middle; margin: auto"
          />삭제</a-button
        >
        <img
          class="cover-image"
          :src="result.smallImg"
          onerror="this.src='data:image/png;base64,'"
        />
      </div>
    </template>
    <a-card-meta>
      <template #title>
        <a-row>
          <a-col class="result-detail-title-container">
            <p class="result-detail-title-meta">{{ result.type }} / {{ result.method }}</p>
            <p class="result-detail-title-name">{{ result.name }}</p>
            <p class="result-detail-title-calorie">{{ result.calorie }} 칼로리</p>
          </a-col>
        </a-row>
      </template>
    </a-card-meta>
  </a-card>
</template>

<script>
import UserInfo from '../../store/models/UserInfo.js';
import Recipe from '../../store/models/Recipe.js';
import {
  CheckCircleOutlined,
  CloseCircleOutlined,
  EditOutlined,
  DeleteOutlined
} from '@ant-design/icons-vue';
export default {
  emits: ['show-detail', 'show-error', 'update-recipe'],
  props: ['result', 'bordered'],
  components: {
    CheckCircleOutlined,
    CloseCircleOutlined,
    EditOutlined,
    DeleteOutlined
  },
  computed: {
    getUser() {
      if (UserInfo.all().length === 0) {
        return {
          userId: null,
          nickname: null,
          token: null,
          favoriteRecipe: []
        };
      } else {
        return UserInfo.all()[0];
      }
    },
    isLoggedIn() {
      return !!this.getUser.token;
    },
    userFavoriteRecipe() {
      return Array.from(this.getUser.favoriteRecipe);
    },
    isInCart() {
      if (this.userFavoriteRecipe.length > 0) {
        for (let index in this.userFavoriteRecipe) {
          if (this.userFavoriteRecipe[index] == this.result.id) {
            return index;
          }
        }
      }
      return false;
    },
    toggleCartLabel() {
      if (this.isInCart) {
        return '장바구니 제외';
      } else {
        return '장바구니 추가';
      }
    },
    isCreator() {
      if (this.getUser.userId === this.result.creator[0]) {
        return true;
      } else {
        return false;
      }
    }
  },
  methods: {
    showDetail() {
      this.$emit('show-detail', this.result.id);
    },
    showError(error) {
      this.$emit('show-error', error);
    },
    async toggleCart() {
      UserInfo.commit((state) => {
        state.fetching = true;
      });
      let prevFavoriteRecipe = Array.from(UserInfo.all()[0].favoriteRecipe);
      if (this.isInCart !== false) {
        prevFavoriteRecipe.splice(this.isInCart, 1);
        if (prevFavoriteRecipe === []) {
          prevFavoriteRecipe = '';
        }
        const payload = {
          userId: this.getUser.userId,
          token: this.getUser.token,
          data: { favoriteRecipe: prevFavoriteRecipe }
        };
        const fetchResult = await UserInfo.api().toggleCart(payload);
        if (fetchResult.response.data.error) {
          this.showError('레시피를 장바구니에서 삭제하는 데 실패하였습니다!');
        }
      } else {
        if (this.userFavoriteRecipe.length === 0) {
          prevFavoriteRecipe = [];
        }
        prevFavoriteRecipe.push(+this.result.id);
        const payload = {
          userId: this.getUser.userId,
          token: this.getUser.token,
          data: { favoriteRecipe: prevFavoriteRecipe }
        };
        const fetchResult = await UserInfo.api().toggleCart(payload);
        if (fetchResult.response.data.error) {
          this.showError('레시피를 장바구니에 추가하는 데 실패하였습니다!');
        }
      }
      const fetchResult = await UserInfo.api().fetch(this.getUser.userId, this.getUser.token);
      if (fetchResult.response.data.error) {
        this.showError('사용자 정보를 불러올 수 없습니다!');
      }
      UserInfo.commit((state) => {
        state.fetching = false;
      });
    },
    updateRecipe() {
      this.$emit('update-recipe', this.result.name);
    },
    async deleteRecipe() {
      Recipe.commit((state) => {
        state.fetching = true;
      });
      const fetchResult = await Recipe.api().deleteRecipe(this.result.id, this.getUser.token);
      if (fetchResult.response.status !== 200) {
        this.showError('레시피를 삭제하는 데 실패하였습니다!');
      }
      Recipe.commit((state) => {
        state.fetching = false;
      });
    }
  }
};
</script>

<style scoped>
@import url('https://fonts.googleapis.com/css2?family=Jua&display=swap');
.result-element-container {
  box-shadow: 0 2px 6px 0 rgb(0 0 0 / 15%);
  width: 100%;
}

.card-cover-container {
  display: inline-block;
  width: 100%;
  position: relative;
}

.toggle-cart-button {
  position: absolute;
  width: fit-content;
  height: fit-content;
  padding-left: 10px;
  padding-right: 10px;
  top: 5px;
  right: 5px;
  font-size: 16px;
}

.update-recipe-button {
  position: absolute;
  width: fit-content;
  height: fit-content;
  padding-left: 10px;
  padding-right: 10px;
  bottom: 5px;
  left: 5px;
  font-size: 16px;
}

.delete-recipe-button {
  position: absolute;
  width: fit-content;
  height: fit-content;
  padding-left: 10px;
  padding-right: 10px;
  bottom: 5px;
  right: 5px;
  font-size: 16px;
}

.cover-image {
  display: block;
  margin: 0px auto;
  width: 100%;
  height: 200px;
}

.result-detail-title-container {
  width: 100%;
}

.result-detail-title-meta,
.result-detail-title-name,
.result-detail-title-calorie,
.result-detail-description {
  display: block;
  width: 100%;
  text-align: left;
  margin: 0px auto;
}

.result-detail-title-meta {
  font-size: large;
  font-weight: bold;
  color: #f47b0f;
}

.result-detail-title-name {
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  font-size: xx-large;
  font-family: 'Jua', sans-serif;
  text-align: center;
}

.result-detail-title-calorie {
  font-size: large;
  color: #f47b0f;
  text-align: right;
  font-weight: bold;
}

@media (min-width: 640px) {
  .result-detail-title-meta {
    font-size: medium;
  }

  .result-detail-title-name {
    font-size: x-large;
  }

  .result-detail-title-calorie {
    font-size: medium;
  }
}
</style>
